package testutils

import (
	"encoding/hex"
	"math"
	"math/big"
	"strings"
	"testing"

	"github.com/ethereum/go-ethereum/accounts/abi"
	gethCommon "github.com/ethereum/go-ethereum/common"
	"github.com/onflow/atree"
	"github.com/onflow/flow-go/fvm/flex/evm"
	"github.com/onflow/flow-go/fvm/flex/models"
	"github.com/onflow/flow-go/fvm/flex/storage"
	"github.com/onflow/flow-go/model/flow"
	"github.com/stretchr/testify/require"
)

type TestContract struct {
	Code       string
	ABI        string
	ByteCode   []byte
	DeployedAt models.FlexAddress
}

func (tc *TestContract) MakeStoreCallData(t *testing.T, num *big.Int) []byte {
	abi, err := abi.JSON(strings.NewReader(tc.ABI))
	require.NoError(t, err)
	store, err := abi.Pack("store", num)
	require.NoError(t, err)
	return store
}

func (tc *TestContract) MakeRetrieveCallData(t *testing.T) []byte {
	abi, err := abi.JSON(strings.NewReader(tc.ABI))
	require.NoError(t, err)
	retrieve, err := abi.Pack("retrieve")
	require.NoError(t, err)
	return retrieve
}

func (tc *TestContract) MakeBlockNumberCallData(t *testing.T) []byte {
	abi, err := abi.JSON(strings.NewReader(tc.ABI))
	require.NoError(t, err)
	blockNum, err := abi.Pack("blockNumber")
	require.NoError(t, err)
	return blockNum
}

func (tc *TestContract) SetDeployedAt(deployedAt models.FlexAddress) {
	tc.DeployedAt = deployedAt
}

func GetTestContract(t *testing.T) *TestContract {
	byteCodes, err := hex.DecodeString("")
	require.NoError(t, err)
	return &TestContract{
		Code: `
			contract Storage {
				uint256 number;
				constructor() payable {
				}
				function store(uint256 num) public {
					number = num;
				}
				function retrieve() public view returns (uint256){
					return number;
				}
				function blockNumber() public view returns (uint256) {
					return block.number;
				}
				function blockTime() public view returns (uint) {
					return  block.timestamp;
				}
				function blockHash(uint num)  public view returns (bytes32) {
					return blockhash(num);
				}
			}
		`,

		ABI: `
		[
			{
				"inputs": [],
				"stateMutability": "payable",
				"type": "constructor"
			},
			{
				"inputs": [
					{
						"internalType": "uint256",
						"name": "num",
						"type": "uint256"
					}
				],
				"name": "blockHash",
				"outputs": [
					{
						"internalType": "bytes32",
						"name": "",
						"type": "bytes32"
					}
				],
				"stateMutability": "view",
				"type": "function"
			},
			{
				"inputs": [],
				"name": "blockNumber",
				"outputs": [
					{
						"internalType": "uint256",
						"name": "",
						"type": "uint256"
					}
				],
				"stateMutability": "view",
				"type": "function"
			},
			{
				"inputs": [],
				"name": "blockTime",
				"outputs": [
					{
						"internalType": "uint256",
						"name": "",
						"type": "uint256"
					}
				],
				"stateMutability": "view",
				"type": "function"
			},
			{
				"inputs": [],
				"name": "retrieve",
				"outputs": [
					{
						"internalType": "uint256",
						"name": "",
						"type": "uint256"
					}
				],
				"stateMutability": "view",
				"type": "function"
			},
			{
				"inputs": [
					{
						"internalType": "uint256",
						"name": "num",
						"type": "uint256"
					}
				],
				"name": "store",
				"outputs": [],
				"stateMutability": "nonpayable",
				"type": "function"
			}
		]
		`,
		ByteCode: byteCodes,
	}
}

func RunWithDeployedContract(t *testing.T, led atree.Ledger, flexRoot flow.Address, f func(*TestContract)) {
	tc := GetTestContract(t)
	// deploy contract
	db, err := storage.NewDatabase(led, flexRoot)
	require.NoError(t, err)

	e := evm.NewEmulator(evm.NewConfig(), db)

	caller := models.NewFlexAddress(gethCommon.Address{})
	_, err = e.MintTo(caller, new(big.Int).Mul(big.NewInt(1e18), big.NewInt(1000)))
	require.NoError(t, err)

	res, err := e.Deploy(caller, tc.ByteCode, math.MaxUint64, big.NewInt(0))
	require.NoError(t, err)

	tc.SetDeployedAt(res.DeployedContractAddress)
	f(tc)
}
